select `pl`.`pr_id` AS `pr_id`,`pl`.`pr_code` AS `pr_code`,`pl`.`pr_name` AS `pr_name`,`pl`.`pr_type` AS `pr_type`,`pl`.`pr_desc` AS `pr_desc`,`pl`.`pr_price` AS `pr_price`,`pl`.`pr_per` AS `pr_per`,`pl`.`pr_qty` AS `pr_qty`,`pl`.`pr_dnt` AS `pr_dnt`,`pl`.`pr_valid` AS `pr_valid`,`pls`.`sh_pr_id` AS `sh_pr_id`,`pls`.`sh_pr_id_rel_pr_id` AS `sh_pr_id_rel_pr_id`,`pls`.`sh_qty` AS `sh_qty`,`pls`.`sh_located` AS `sh_located`,`pls`.`sh_dnt` AS `sh_dnt`,`pls`.`sh_valid` AS `sh_valid` from (`stwl_db`.`sw_products_list` `pl` left join `stwl_db`.`sw_products_list_show` `pls` on((`pl`.`pr_id` = `pls`.`sh_pr_id_rel_pr_id`))) where (isnull(`pls`.`sh_pr_id_rel_pr_id`) or ((`pls`.`sh_pr_id_rel_pr_id` = `pl`.`pr_id`) and (`pl`.`pr_valid` = 1) and (coalesce(`pls`.`sh_valid`,1) = 1) and (coalesce(`pls`.`sh_qty`,0) >= 0))) order by `pl`.`pr_dnt`